protected void preRunModifyContract()
{
    HMPatientNameAndAddressDataContract contract;
    super();

    contract = this.parmReportContract().parmRdpContract() as HMPatientNameAndAddressDataContract;

    contract.parmPatientAccount(CustTable::findRecId(this.parmArgs().record().RecId).AccountNum);
    contract.parmLogisticsAddressing(this.parmArgs().caller().getPatientSelectedAddress());


   //this.parmReportContract().parmPrintSettings().numberOfCopies(10);
}

public static client void main(Args args)
{

    //define the new object for controller class

    HMPatientNameAndAddressReportController HMPatientNameAndAddressReportController;

    HMPatientNameAndAddressReportController = new HMPatientNameAndAddressReportController();

    //pass the caller args to the controller

    HMPatientNameAndAddressReportController.parmArgs(args);

    //set the report name and report design to run

    HMPatientNameAndAddressReportController.parmReportName(ssrsReportStr(HMPatientNameAndAddress,PrecisionDesign1));

    //execute the report

    HMPatientNameAndAddressReportController.startOperation();

}
