public void createAppointmentOrder(ItemId _itemId,boolean _defaultOrder)
{
    hmAppointmentInventTmp.ItemId = _itemId;
    hmAppointmentInventTmp.ItemName = HMInventTableClass::showDescription(_itemId);
    hmAppointmentInventTmp.EcoResProduct = InventTable::find(_itemId).Product;
    hmAppointmentInventTmp.EcoResProductName = EcoResProduct::find(hmAppointmentInventTmp.EcoResProduct).productName();
    hmAppointmentInventTmp.DefaultOrder = _defaultOrder;
    hmAppointmentInventTmp.insert();
}

public boolean createENNOUService(RecId _hmClinicStaffRecId, ItemId _itemId)
{
