public  void updateDirParty(RecId _recid)
{

   DirPartyTable dirPartyTable;
   ttsBegin;
   select forUpdate  dirPartyTable
       where dirPartyTable.RecId == _recid;

   dirPartyTable.LanguageId = "ar";


            dirPartyTable.update();
            ttsCommit;
}

public static void updatePatientRefusedToProvidePicture(RecId _hmPatientRecid , boolean _RefusedToProvidePicture)
{

   HMPatient hMPatient;

   select forUpdate  hMPatient
       where hMPatient.RecId == _hmPatientRecid;
       if(hMPatient)
       {
           if(_RefusedToProvidePicture)
           {
               hMPatient.RefusedToProvidePicture = NoYes::Yes;
           }
           else
           {
               hMPatient.RefusedToProvidePicture = NoYes::No;
           }

           ttsBegin;
           hMPatient.update();
           ttsCommit;

       }




}
