SourceCode.Declaration:
[SRSReportQueryAttribute(querystr(HMPatientRegistrationCard))]
public class HMPatientRegistrationCardDP extends SRSReportDataProviderBase
{
    HMPatientRegistrationCardTmp tempTable;
}

Method.Source:
[SRSReportDataSetAttribute(tablestr(HMPatientRegistrationCardTmp))]
public HMPatientRegistrationCardTmp getHMPatientRegistrationCardTmp()
{
    select * from tempTable;
    return  tempTable;
}

public void processReport()
{
    HMPatient                   hmPatient;
    QueryRun        queryRun;

    //this.parmQuery().dataSourceTable(tableNum(HMPatient)).addRange(fieldNum(HMPatient, RecId)).value(int642str(5637144834));

    queryRun = new queryRun(this.parmQuery());
    while (queryRun.Next())
    {
        hmPatient = queryRun.get(tableNum(HMPatient));

        tempTable.Gender = hmPatient.Gender;
        tempTable.NameSuffix = hmPatient.NameSuffix;
        tempTable.PatientRecId = hmPatient.RecId;
        tempTable.RegistrationDate = hmPatient.RegistrationDate;
        tempTable.Salutation = hmPatient.Salutation;
        tempTable.SocialSecurityNumber = hmPatient.SocialSecurityNumber;
        tempTable.Status = hmPatient.Status;
        tempTable.PrimaryContact = hmPatient.getPrimaryContact();
        tempTable.PatientBirthDate = hmPatient.patientBirthDate();
        tempTable.PatientChartNum = hmPatient.patientChartNum();
        tempTable.PatientNameWithSalutation = hmPatient.patientNameWithSalutation();
        //breakpoint;
        tempTable.PatientImage = hmPatient.patientImage();

        tempTable.insert();
    }
}
