/// <summary>
/// Assigns the value for <c>HMPatientRegistrationForm</c> SRS report hidden ranges.
/// </summary>
class HMPatientRegistrationController extends SrsReportRunController
{
    HMPatientRegistrationContract       hmPatientRegistrationContract;
    HMPatient                           hmPatient;
}

Method.Source:
public void prePromptModifyContract()
{
    hmPatientRegistrationContract = this.parmReportContract().parmRdpContract() as HMPatientRegistrationContract;

    hmPatient = HMPatient::find(this.parmArgs().record().RecId);

    hmPatientRegistrationContract.parmPatient(hmPatient.RecId);

    SrsReportHelper::addParameterValueRangeToQuery(this.getFirstQuery(),tableNum(HMPatient),fieldNum(HMPatient, RecId),SysQuery::value(hmPatient.RecId));
}

