protected void preRunModifyContract()
{
    //define object for report contract
    HMPatientStickerReportContract contract;

    //get the reference of the current contract object
    contract = this.parmReportContract().parmRdpContract() as HMPatientStickerReportContract;

    //modify the parameter value of the contract
    contract.parmCaseNo(HMCase::find(this.parmArgs().record().RecId).CaseNo);
}

public static client void main(Args args)
{
    //HMResourceSchedulingAppointment appt;

    //define the new object for controller class
    HMPatientStickerReportController hmPatientStickerReportController;

    hmPatientStickerReportController = new HMPatientStickerReportController();

    //pass the caller args to the controller
    hmPatientStickerReportController.parmArgs(args);
    hmPatientStickerReportController.parmReportName(ssrsReportStr(HMPatientStickerReport, PatientStickerReport));

    //execute the report
    hmPatientStickerReportController.startOperation();
}
