Method.Source:
    /// <summary>
    /// Runs the class with the specified arguments.
    /// </summary>
    /// <param name = "_args">The specified arguments.</param>
    public static void main(Args _args)
    {      
        HMBillParameters billParameters = HMBillParameters::find();

        if (billParameters.HMEnablePreInvoice)
        {
            new MenuFunction(menuitemDisplayStr(HMPaymentCenter), MenuItemType::Display).run(_args);
		}
		else
        {
            new MenuFunction(menuitemDisplayStr(HMPaymentCenter_US), MenuItemType::Display).run(_args);
		}
    }
