[SysClientCacheDataMethodAttribute(true)]
public static display HMContractNumber insuranceContract(PriceDiscTable _this)
{
    PriceDiscAdmTrans priceDiscAdmTransBuffer;
    HMInsuranceContract hmInsuranceContract;

    select priceDiscAdmTransBuffer
        where priceDiscAdmTransBuffer.RecId == _this.OriginalPriceDiscAdmTransRecId;

    if(priceDiscAdmTransBuffer)
    {
        select hmInsuranceContract
        where hmInsuranceContract.RecId == priceDiscAdmTransBuffer.HMInsuranceContract;
    }


    return hmInsuranceContract.HMContractNumber;
}

