[SysClientCacheDataMethodAttribute(true)]
public static display HMProcedureMasterCode cptCode(PriceDiscTable _this)
{
    PriceDiscAdmTrans priceDiscAdmTransBuffer;
    HMProcedureCodeMaster cptCode;

    select priceDiscAdmTransBuffer
        where priceDiscAdmTransBuffer.RecId == _this.OriginalPriceDiscAdmTransRecId;

    if(priceDiscAdmTransBuffer)
    {
        select cptCode
        where cptCode.Code == priceDiscAdmTransBuffer.HMCPTHCPCCode
            && cptCode.Type == HMProcCodeSearchType::CPT_HCPS;
    }

    return cptCode.Code;
}

[SysClientCacheDataMethodAttribute(true)]
public static display HMProcedureMasterCode revenueCode(PriceDiscTable _this)
{
    PriceDiscAdmTrans priceDiscAdmTransBuffer;
    HMProcedureCodeMaster cptCode;

    select priceDiscAdmTransBuffer
        where priceDiscAdmTransBuffer.RecId == _this.OriginalPriceDiscAdmTransRecId;

    if(priceDiscAdmTransBuffer)
    {
        select cptCode
        where cptCode.Code == priceDiscAdmTransBuffer.HMRevenueCode
            && cptCode.Type == HMProcCodeSearchType::Revenue;
    }

    return cptCode.Code;
}

[SysClientCacheDataMethodAttribute(true)]
public static display str caseType(PriceDiscTable _this)
{
    PriceDiscAdmTrans priceDiscAdmTransBuffer;

    select priceDiscAdmTransBuffer
        where priceDiscAdmTransBuffer.RecId == _this.OriginalPriceDiscAdmTransRecId;

    return enum2Str(priceDiscAdmTransBuffer.HMCaseType);
}

