protected void preRunModifyContract()
{
    InventTable inventTable = args.record();;
    SrsReportHelper::addParameterValueRangeToQuery(this.getFirstQuery(),tableNum(InventTable),fieldNum(InventTable, ItemId),SysQuery::value(inventTable.ItemId));
    super();
}

