protected void prePromptModifyContract()
{
    HMClaimTable                claimTable;
    HMProformaInvoiceContract   contract;

    if (this.parmArgs() && this.parmArgs().record())
    {
        contract    = this.parmReportContract().parmRdpContract() as HMProformaInvoiceContract;
        claimTable  = this.parmArgs().record();

        contract.parmClaimTableRecId(claimTable.RecId);

        this.parmReportContract().parmRdpContract(contract);
    }
}

public static void main(Args _args)
{
    HMProformaInvoiceController    controller = new HMProformaInvoiceController();
    HMClaimTable        claim;

    claim = _args.record() as HMClaimTable;

    controller.parmArgs(_args);
    controller.parmReportName(ssrsReportStr(HMProformaInvoiceReport, Design));
    controller.parmShowDialog(false);
    controller.startOperation();
}
