Method.Source:
public void lookupVender(FormStringControl _ctrl)
{
    Query query = new Query();
    QueryBuildDataSource qbds_CustTable;
    SysTableLookup sysTableLookup;
    QueryBuildRange qbr;
    //super();
    sysTableLookup = SysTableLookup::newParameters(tablenum(VendTable), _ctrl);
    qbds_CustTable = query.addDataSource(tableNum(VendTable));
    sysTableLookup.addLookupfield(fieldnum(VendTable, AccountNum), true);
    sysTableLookup.addLookupMethod(tableMethodStr(VendTable, name));
    //sysTableLookup.addLookupfield(fieldnum(CustTable, ClientBillingType));
    //qbr = qbds_CustTable.addRange(fieldNum(CustTable, ClientBillingType));
    //qbr.value(SysQuery::value(HMClientBillingType::MonthlyInvoice));
    sysTableLookup.parmUseLookupValue(false);
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void build()
{
    Dialog  dialogLocal;

    dialogLocal = this.dialog();

    dataContract     = this.dataContractObject();

    grpCust = dialogLocal.addGroup("Vendor account");//"@SYS7149");
    VendAccountField = this.addDialogField(methodStr(HMPurchaseOrderInvoiceDataContract, parmVendAccount), dataContract);
    VendAccountField.label("Vender account"); 
    grpDate = dialogLocal.addGroup("Date");//"@MED505");
    fromDateField    = this.addDialogField(methodStr(HMPurchaseOrderInvoiceDataContract, parmFromDate), dataContract);
    fromDateField.label("Date");
    //toDateField      = this.addDialogField(methodStr(HMClientMonthlyInvoiceBatchDataContract, parmToDate), dataContract);
}

public void postBuild()
{
    DialogField dialogField;
    Dialog dialogLocal = this.dialog();

    super();

    dialogField = this.bindInfo().getDialogField(this.dataContractObject(), methodStr(HMPurchaseOrderInvoiceDataContract, parmVendAccount));
    dialogField.registerOverrideMethod(methodStr(FormStringControl, lookup), methodStr(HMPurchaseOrderInvoice_GuranteedUIBuilder, lookupVender), this);
}
