SourceCode.Declaration:
[DataContractAttribute]
class HMReasonContract
{
    str         reasonCode;
    str         reasonDescription;
    RefRecId    recId;
}

Method.Source:
[ DataMemberAttribute('ReasonCode')]
str parmReasonCode(str _reasonCode = reasonCode)
{
    reasonCode = _reasonCode;

    return reasonCode;
}

[ DataMemberAttribute('ReasonDescription')]
str parmReasonDescription(str _reasonDescription = reasonDescription)
{
    reasonDescription = _reasonDescription;

    return reasonDescription;
}

[ DataMemberAttribute('RecId')]
RefRecId parmRecId(RefRecId _recId = recId)
{
    recId = _recId;

    return recId;
}
