public static void copyDayPlan(RecId _dayPlanRecId, str _code, str _description)
{
    HMResourceSchedulingDayPlan         sourceDayPlan, copyDayPlan;
    HMResourceSchedulingDayPlanItem     sourceDayPlanItem, copyDayPlanItem;
    RecordInsertList                    copyDayPlanItemList = new RecordInsertList(tableNum(HMResourceSchedulingDayPlanItem));
    try
    {
        ttsBegin;
        select * from sourceDayPlan
        where sourceDayPlan.RecId == _dayPlanRecId;
        buf2Buf(sourceDayPlan,copyDayPlan);
        copyDayPlan.Code = _code;
        copyDayPlan.Description = _description;
        copyDayPlan.insert();

        while select * from sourceDayPlanItem
            where sourceDayPlanItem.HMResourceSchedulingDayPlan == sourceDayPlan.RecId
        {
            buf2Buf(sourceDayPlanItem,copyDayPlanItem);
            copyDayPlanItem.HMResourceSchedulingDayPlan = copyDayPlan.RecId;
            copyDayPlanItemList.add(copyDayPlanItem);
            copyDayPlanItem.clear();
        }
        copyDayPlanItemList.insertDatabase();
        ttsCommit;
        info("@MAZ11354");
    }
    catch
    {
        warning("@MAZ11355");
    }

}

public static RecId getOrganizationalUnitRecId(RecId _planningObjectRecId)
{
    HMResourceSchedulingPlanningObject hmResourceSchedulingPlanningObject;

    select HMOrganizationalUnit from hmResourceSchedulingPlanningObject
        where hmResourceSchedulingPlanningObject.RecId == _planningObjectRecId;

    return hmResourceSchedulingPlanningObject.HMOrganizationalUnit;
}
