public static void main(Args _invoiceIds, str filePath)
{
    HMSalesInvoiceDataContract HMSalesInvoiceDataContract;
    HMSalesInvoiceController    hmSalesInvoiceController;
    SRSPrintDestinationSettings     settings;
    CustInvoiceJour                 custInvoiceJour;
    CustTable custTable;

    HMSalesInvoiceDataContract contract = new HMSalesInvoiceDataContract();
    hmSalesInvoiceController = new HMSalesInvoiceController();

    custInvoiceJour = _invoiceIds.record();

    hmSalesInvoiceController.parmReportName(ssrsReportStr(HMSalesInvoice,DesignENFR));
    hmSalesInvoiceController.parmArgs(_invoiceIds);
    hmSalesInvoiceController.parmShowDialog(false);
    hmSalesInvoiceController.parmLoadFromSysLastValue(false);

    settings = hmSalesInvoiceController.parmReportContract().parmPrintSettings();
    settings.fileFormat(SRSReportFileFormat::PDF);
    settings.printMediumType(SRSPrintMediumType::File);
    settings.fileName(filePath);
    settings.overwriteFile(true);

    hmSalesInvoiceController.startOperation();
    hmSalesInvoiceController.parmLoadFromSysLastValue(false);
}
