protected void postCustVend()
{
    CustTrans   custTrans = custInvoiceJour.custTrans();
    if (custInvoiceJour.HMAppointmentNumber)
    {
        custTrans = CustTrans::find(custTrans.RecId, true);
        ttsBegin;
        custTrans.HMAppointmentNumber = custInvoiceJour.HMAppointmentNumber;
        custTrans.update();
        ttsCommit;
    }
}
