SourceCode.Declaration:
class HMSalesInvoiceUIBuilder extends SrsReportDataContractUIBuilder
{
    DialogField dlgInvoicePrint;
}

Method.Source:
public void postBuild()
{
    HMSalesInvoiceDataContract contract;

    super();

    //get the field to override by providing the data contract object and the    associated attribute/method
    dlgInvoicePrint = this.bindInfo().getDialogField(this.dataContractObject(),
                methodStr(HMSalesInvoiceDataContract,parmClientInvoicePrint));

    contract = this.dataContractObject();

    dlgInvoicePrint.visible(!contract.parmIsReceipt());
}
