Method.Source:
str caption()
{
    HMSmsTable table = HMSmsTable::find(id);

    return "SMS Editor" + ' ' + table.description + ' ' + (_language ? '(' + _language + ')'  : '');
}

void new(SysEmailId _id, LanguageId _languageId)
{
    HMSmsMessageTable message;

    super(_id, _languageId);

    // Set the initial text:
    ttsbegin;
    message = HMSmsMessageTable::find(_id, _languageId, true);
    ttscommit;

    this.text(message.mail);
}

public void save()
{
    HMSmsMessageTable message;

    ttsBegin;
    message = HMSmsMessageTable::find(id, _language, TRUE);
    message.mail = this.text();
    message.update();
    ttsCommit;
}

static void main(Args args)
{
    HMSmsMap  message = args.record();

    HMSmsEditor e = new HMSmsEditor(message.SmsId, message.languageId);
    e.run();
}
