public static void main(Args _args)
{
    HMTransactionValidationController controller;
    controller = HMTransactionValidationController::newFromArgs(_args);
    controller.startOperation();
}

public static HMTransactionValidationController newFromArgs(Args _args)
{
    HMTransactionValidationController   controller;
    HMTransactionValidationContract     dataContract;

    controller = new HMTransactionValidationController();
    controller.initializeFromArgs(_args);

    dataContract = controller.getDataContractObject('_dataContract');

    // Set default values
    dataContract.parmTransDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));

    return controller;
}

public void new()
{
    super();

    // Specify the service class and operation method
    this.parmClassName(classStr(HMTransactionValidationService));
    this.parmMethodName(methodStr(HMTransactionValidationService, processTransaction));
    this.parmDialogCaption("Transaction Validation Service");
}
