public void build()
{
    Dialog                              dialogLocal = this.dialog();
    HMTransactionValidationContract     contract = this.dataContractObject();

    // Add Transaction Information group
    dialogLocal.addGroup("Transaction Information");
    this.addDialogField(methodStr(HMTransactionValidationContract, parmTransDate), contract);

    // Add Account Information group
    dialogLocal.addGroup("Account Information");
    this.addDialogField(methodStr(HMTransactionValidationContract, parmCustomerAccount), contract);
    this.addDialogField(methodStr(HMTransactionValidationContract, parmVendorAccount), contract);
}

