[ AifCollectionTypeAttribute('return', Types::Class, classstr(HMUserContract))]
public HMUserContract GetUserLogin(str _userName)
{
    HMUserContract      userContract = new HMUserContract();
    userContract.populateUserContract(_userName);

    return userContract;
}

[ AifCollectionTypeAttribute('return', Types::Class, classstr(HMUserContract))]
public List GetUsers(int _userParam)
{
    List userList = new List(Types::Class);
    UserExternalParty   user;
    HMUserContract userContract;

    switch(_userParam)
    {
        case 0:
            while select user
                where user.ExternalEntityType == UserExternalPartyEntityType::Vendor
                   || user.ExternalEntityType == UserExternalPartyEntityType::Worker
            {
                userContract = new HMUserContract();
                userContract.populateUserContract(user.User);
                if(userContract.parmUserId() != "")
                {
                    userList.addEnd(userContract);
                }
            }
            break;

        case 1:
            while select user
                where user.ExternalEntityType == UserExternalPartyEntityType::Worker
            {
                userContract = new HMUserContract();
                userContract.populateUserContract(user.User);
                userList.addEnd(userContract);
            }
            break;

        case 2:
            while select user
                where user.ExternalEntityType == UserExternalPartyEntityType::Vendor
            {
                userContract = new HMUserContract();
                userContract.populateUserContract(user.User);
                userList.addEnd(userContract);
            }
            break;
    }

    return userList;
}
