[FormDataSourceEventHandler(formDataSourceStr(HcmApplicant, HcmApplicant), FormDataSourceEventType::ValidatedWrite)]
public static void HcmApplicant_OnValidatedWrite(FormDataSource sender, FormDataSourceEventArgs e)
{
    boolean ret;
    FormRun _sender = sender.formRun() as formrun;
    formDataSource HcmApplicant = sender;
    var args = e as FormDataSourceCancelEventArgs;
    HcmPersonPrivateDetails HcmPersonPrivateDetails = _sender.dataSource(formDataSourceStr(HcmApplicant,HcmPersonPrivateDetails)).cursor();
    if(HcmPersonPrivateDetails.Gender == hcmPersonGender::None)
    {
        warning(strFmt("@SYS84753","@SYS9284"));
        ret =false;
    }
    else
    {
        ret= true;
    }
    args.cancel(!ret);



}
