[FormEventHandler(formStr(HcmWorker), FormEventType::Initialized)]
public static void HcmWorker_OnInitialized(xFormRun sender, FormEventArgs e)
{
    if (isConfigurationkeyEnabled(configurationKeyNum('HMWorkerArabicFields')))
    {
        sender.design().controlName(formControlStr(HcmWorker, HMArabicName)).visible(true);
    }

}
