public void addImage(str _resName,int imageNumber)
{
    container       imageData;
    ResourceNode    resourceNode;
    Image           myImage;
    ResourceName    resourceName = _resName;
    int imageNum;

    resourceNode = SysResource::getResourceNode(resourceName);

    if (resourceNode)
    {
        resourceNode.AOTload();
        imageData = SysResource::getResourceNodeData(resourceNode);
        myImage = new Image();
        myImage.setData(imageData);
        imageNum = imagelist.add(myImage);
        imageConvert.insert(imageNumber, imageNum);
    }

}

protected boolean keepInMemory()
{
    boolean ret;

    ret = false;

    return ret;
}
