public static display CostPriceAverage AvgCostPerUnit(InventJournalTrans _this)
{
    InventSum inventSum;

    select inventSum where inventSum.ItemId==_this.ItemId;
    return inventSum.averageCostPrice();
}

[SysClientCacheDataMethodAttribute(true)]
public static display ItemName Item_Name(InventJournalTrans _this)
{
    return InventTable::find(_this.ItemId).itemName();

}

[SysClientCacheDataMethodAttribute(true)]
public static display HMCaseNo CaseNo(InventJournalTrans _this)
{
    HMCaseTrans caseTrans = HMCaseTrans::find(_this.HMCaseTrans);

    return HMCase::find(caseTrans.HMCase).CaseNo;

}
