Method.Source:
    static void main(Args _args)
{
    VendTable _vendtable;
    HMResourceMaster _master;
    HMResourceMasterSpeciality _speciality;
    HMSpecialtyCode _code;

    while select *from _master
        join _speciality
        join _code where
         _master.RecId == _speciality.HMResource
        &&_speciality.HMSpecialtyCode == _code.RecId
        && _code.SpecialtyCodeId == '001'
    {
        info("Resource Code :"+ _master.Code +  " Speciality Code : " + _code.SpecialtyCodeId+ " Description" + _code.Description);

    }


}
