public static FreeTxt getVendInvoiceNoForPaymentLine(LedgerJournalTrans _this)
{
    SpecTrans       specTrans;
    VendTransOpen   vendTransOpen;
    VendTrans       vendTrans;

    FreeTxt         invoiceNumbers;
    ;

    while select RefRecId from specTrans where specTrans.SpecTableId == _this.TableId &&
                                 specTrans.SpecRecId   == _this.RecId
    {
        select firstonly RefRecId from vendTransOpen where vendTransOpen.RecId == specTrans.RefRecId;

        select firstonly Invoice from vendTrans where vendTrans.RecId == vendTransOpen.RefRecId;

        invoiceNumbers = vendTrans.Invoice;

    }

    return invoiceNumbers;
}
