/// <param name="sender"></param>
/// <param name="e"></param>
[FormDataSourceEventHandler(formDataSourceStr(LogisticsContactInfo, LogisticsElectronicAddress), FormDataSourceEventType::InitValue)]
public static void LogisticsElectronicAddress_OnInitValue(FormDataSource sender, FormDataSourceEventArgs e)
{
    FormRun _formrun =sender.formRun() as formrun;
   _formrun.UpdateContactinfo(sender,e);
}

Public void UpdateContactinfo(FormDataSource sender, FormDataSourceEventArgs e)
{
    formrun FormRun = sender.formRun() as FormRun;
    FormDataSource fds = FormRun.dataSource(formDataSourceStr(LogisticsContactInfo,logisticsElectronicAddress)) as FormDataSource;
    LogisticsElectronicAddress  LogisticsElectronicAddress = FormRun.dataSource(formDataSourceStr(LogisticsContactInfo,logisticsElectronicAddress)).cursor();
    if(contactInfoForm.parmOpenedForEmailPrimary())
    {

        LogisticsElectronicAddress.IsPrimary =true;
        logisticsElectronicAddress.Type = LogisticsElectronicAddressMethodType::Email;
        fds.object(fieldNum(logisticsElectronicAddress, Type)).allowEdit(false);
        fds.object(fieldNum(logisticsElectronicAddress, IsPrimary)).allowEdit(false);
    }
}
