static void main(Args _args)
{
    SysDictTable    dictTable = new SysDictTable(tableNum(CustPackingSlipJour));
    SysDictField    dictField;
    TreeNode        treeNode;
    FieldId         fieldId = dictTable.fieldNext(0);

    while (fieldId)
    {
        dictField = dictTable.fieldObject(fieldId);

        if (dictField.isSql() && !dictField.isSystem())
        {
            treeNode = dictField.treeNode();
        //    /
        //info(strFmt("%1 | %2 | %3",
        //    dictField.name(),                                               // Field name
        //    treeNode.AOTgetProperty("Label"),                               // Label id
        //    SysLabel::labelId2String(treeNode.AOTgetProperty("Label"))));   // Label string
        //    /
            if (treeNode.AOTgetProperty("Mandatory") == enum2str(NoYes::Yes))
            {
                info(dictField.name());
            }
        }
        fieldId = dictTable.fieldNext(fieldId);
    }
}
