[SRSReportParameterAttribute(classstr(MZKWarehouseLabelContract))]
public class MZKWarehouseLabelDP extends SRSReportDataProviderBase
{
    MZKWarehouseLabelTmp   warehouseLabelTmp;



}

Method.Source:
/// <summary>
///
/// </summary>
[SRSReportDataSetAttribute('MZKWarehouseLabelTmp')]
public MZKWarehouseLabelTmp getMZKWarehouseLabelTmp()
{
    select warehouseLabelTmp;
    return warehouseLabelTmp;
}

/// <summary>
///
/// </summary>
public void processReport()
{
    MZKWarehouseLabelContract   contract;
    PackingSlipId               packingSlipId;
    MZKWarehouseLabel           mzkWarehouseLabel;



    contract = this.parmDataContract() as MZKWarehouseLabelContract;
    packingSlipId = contract.parmPackingSlipId();

    select firstonly mzkWarehouseLabel
        where mzkWarehouseLabel.PackingSlipId == packingSlipId;

    if (mzkWarehouseLabel)
    {
        warehouseLabelTmp.PackingSlipId = mzkWarehouseLabel.PackingSlipId;
        warehouseLabelTmp.SalesId = mzkWarehouseLabel.SalesId;
        warehouseLabelTmp.KitSerial = mzkWarehouseLabel.KitSerial;
        warehouseLabelTmp.MZKLabel = strFmt("%1_%2_%3", mzkWarehouseLabel.SalesId, mzkWarehouseLabel.PackingSlipId, mzkWarehouseLabel.KitSerial);
        warehouseLabelTmp.insert();
    }

}
