Method.Source:
Public static Display Name GetMainAccountTranslation(MainAccount _mainAccount)
 {
     Name name;

     MainAccountTranslation  MainAccountTranslation;

     select MainAccountTranslation
         where MainAccountTranslation.MainAccount == _mainAccount.RecId
             && MainAccountTranslation.LanguageId == "ar-sa" ;

     name = MainAccountTranslation.Name;

     return name;
 }
