/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormControlEventHandler(formControlStr(SalesTableListPage, buttonUpdateConfirmation), FormControlEventType::Clicked)]
public static void buttonUpdateConfirmation_OnClicked(FormControl sender, FormControlEventArgs e)
{
    MzkSalesTableEventHandler::createProductionOrder(sender, e);
}

/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormControlEventHandler(formControlStr(SalesTable, buttonUpdateConfirmation), FormControlEventType::Clicked)]
public static void buttonUpdateConfirmationList_OnClicked(FormControl sender, FormControlEventArgs e)
{        
    MzkSalesTableEventHandler::createProductionOrder(sender, e);
}

 public static void createProductionOrder(FormControl sender, FormControlEventArgs e)
 {

    FormRun         formRun         = sender.formRun();
    FormDataSource  SalesTable_ds   = formRun.dataSource(formDataSourceStr(SalesTable, SalesTable)) as FormDataSource;
    SalesTable      salesTable      = SalesTable_ds.cursor();
    MzkProductionOrder prodOrder        = new MzkProductionOrder();
    //info(salesTable.SalesId);
    //info(enum2Str(salesTable.DocumentStatus));

    if (salesTable.DocumentStatus == DocumentStatus::Confirmation)
    {
        prodOrder.createProductionOrderFromSalesOrder(salesTable.SalesId);
    }
}
