Method.Source:
/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormEventHandler(formStr(OMOperatingUnit), FormEventType::Initialized)]
public static void OMOperatingUnit_OnInitialized(xFormRun sender, FormEventArgs e)
{
    if(sender.args().menuItemName() == menuitemDisplayStr(OmOperatingUnitMedisys))
    {
        sender.design().caption('Clinics');
    }
}

/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormDataSourceEventHandler(formDataSourceStr(OMOperatingUnit, OMOperatingUnit), FormDataSourceEventType::InitValue)]
public static void OMOperatingUnit_OnInitValue(FormDataSource sender, FormDataSourceEventArgs e)
{
    OMOperatingUnit omOperatingUnit = sender.cursor();
    if (sender.formRun().args().menuItemName() == menuitemDisplayStr(OMOperatingUnitMedisys))
    {
        omOperatingUnit.Medisys = NoYes::Yes;
    }
}

/// <summary>
///
/// </summary>
/// <param name="args"></param>
[PostHandlerFor(formStr(OMOperatingUnit), formMethodStr(OMOperatingUnit, UpdateButtonNames))]
public static void OMOperatingUnit_Post_UpdateButtonNames(XppPrePostArgs args)
{
    FormRun formRun = args.getThis() as FormRun;

    FormStringControl omOperatingUnitNumber = formRun.design().controlName(formControlStr(OMOperatingUnit, omOperatingUnitNumber));

    str operatingUnitNumberText = strFmt("@SYS317182", 'Clinic');
    str operatingUnitNumberHelpText = strFmt( "@SYS317183", 'Clinic');


    omOperatingUnitNumber.text(operatingUnitNumberText);
    omOperatingUnitNumber.label(operatingUnitNumberText);

    omOperatingUnitNumber.helpText(operatingUnitNumberHelpText);
}

/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormDataSourceEventHandler(formDataSourceStr(OMOperatingUnit, OMOperatingUnit), FormDataSourceEventType::Initialized)]
public static void OMOperatingUnit_OnInitializedHM(FormDataSource sender, FormDataSourceEventArgs e)
{
    FormRun formRun = sender.formRun();
    if(formRun.args().menuItemName() == menuitemDisplayStr(OmOperatingUnitMedisys))
    {
        sender.queryBuildDataSource().addRange(fieldNum(OMOperatingUnit, Medisys)).value(SysQuery::value(NoYes::Yes));
    }
}
