    /// <summary>
    ///
    /// </summary>
    /// <param name="sender"></param>
    /// <param name="e"></param>
    [DataEventHandler(tableStr(PrintMgmtDocInstance), DataEventType::ValidatingWrite)]
    public static void PrintMgmtDocInstance_onValidatingWrite(Common sender, DataEventArgs e)
    {
        PrintMgmtDocInstance table = sender as PrintMgmtDocInstance;
        ValidateEventArgs ve = e as ValidateEventArgs;
        boolean ret = ve.parmValidateResult();

		if (table.ReferencedRecId == 0 && table.ReferencedTableId == 0)
        {
            warning("Global print setting is not allowed to save!");
            ret = false;
            ve.parmValidateResult(ret);
		}

    }

