/// <summary>
/// Post event handler for <c>SalesTable</c> <c>SalesLine</c> Initialized event.
/// </summary>
/// <param name = "_sender"></param>
/// <param name = "_e"></param>
[FormDataSourceEventHandler(formDataSourceStr(PurchTable, PurchTable), FormDataSourceEventType::Initialized)]
public static void PurchTable_OnInitialized(FormDataSource _sender, FormDataSourceEventArgs _e)
{
    var overrides = PurchTableFormExtensionOverrides::construct();

    _sender.object(fieldNum(PurchTable, HMCase)).registerOverrideMethod(methodStr(FormDataObject, jumpRef),
        methodStr(PurchTableFormExtensionOverrides, hmCase_OnJumpRef), overrides);

    //_sender.object(fieldNum(SalesLine, ItemId)).registerOverrideMethod(methodStr(FormDataObject, validate),
    //    methodStr(SalesTableFormExtensionOverrides, itemId_OnValidate), overrides);
}

