{

    ReasonTable        reasonTable;
    boolean            ret = false;
    ReasonCode         reasonCode;
    ReasonRefRecID     _reasonRefRecID =Args.getArg("_reasonRefRecID");
    LedgerJournalACType _LedgerJournalACType = Args.getArg("_LedgerJournalACType");
    ;

    reasonCode  = ReasonTableRef::find(_reasonRefRecID).Reason;
    reasonTable = ReasonTable::find(reasonCode);

    if (reasonCode)
    {
        // <MazikCare>
        if (isConfigurationkeyEnabled(configurationKeyNum(MazikCare)))
        {
            switch (_ledgerJournalACType)
            {
                case LedgerJournalACType::MovementCancel:
                    if (reasonTable.MovementCancel == NoYes::Yes)
