public boolean unpack(container packedClass)
{
    Version     version = RunBase::getVersion(packedClass);
    container   queryCon;

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList,queryCon] = packedClass;
            if (SysQuery::isPackedOk(queryCon))
            queryrun = new SysQueryRun(queryCon);
            else
            this.initQuery();
            break;
        default:
            return false;
    }

    return true;
}

