public void refreshAmount()
{
    HMAppointmentEODStatementLines.LineAmount = HMAppointmentEODStatementLines.ChargeCodeFee - HMAppointmentEODStatementLines.LineDisc;
    HMAppointmentEODStatementLines.TaxAmount = Tax::calcTaxAmount(HMAppointmentEODStatementLines.TaxGroup, HMAppointmentEODStatementLines.TaxItemGroup, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), CompanyInfoHelper::standardCurrency(), HMAppointmentEODStatementLines.LineAmount, TaxModuleType::Sales);
    HMAppointmentEODStatementLines.TotalAmount = HMAppointmentEODStatementLines.LineAmount + HMAppointmentEODStatementLines.TaxAmount;
}

public void updateButtons()
{
    HMAppointmentEODStatement_ds.object(fieldNum(HMAppointmentEODStatement,IntervalEndDateTime)).allowEdit(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Open);
    HMAppointmentEODStatement_ds.object(fieldNum(HMAppointmentEODStatement,IntervalStartDateTime)).allowEdit(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Open);
    HMAppointmentEODStatement_ds.object(fieldNum(HMAppointmentEODStatement,HMOrganizationalUnit)).allowEdit(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Open);
    if(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Open)
    {
        HMAppointmentEODStatementClass_CalcStmt.enabled(true);
        HMAppointmentEODStatementClass_PostStmt.enabled(false);
        HMAppointmentEODStatementClass_ClearStmt.enabled(false);
    }
    else if(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Calculated)
    {
        HMAppointmentEODStatementClass_CalcStmt.enabled(false);
        HMAppointmentEODStatementClass_PostStmt.enabled(true);
        HMAppointmentEODStatementClass_ClearStmt.enabled(true);

    }
    else if(HMAppointmentEODStatement.Status == HMAppointmentEODStatementStatus::Posted)
    {
        HMAppointmentEODStatementClass_CalcStmt.enabled(false);
        HMAppointmentEODStatementClass_PostStmt.enabled(false);
        HMAppointmentEODStatementClass_ClearStmt.enabled(false);
    }
}

public int active()
{
    int ret;

    ret = super();
    if(ret)
    {
        element.updateButtons();
        HMAppointmentEODStatement_ds.allowEdit(HMAppointmentEODStatement.Status != HMAppointmentEODStatementStatus::Posted);
        HMAppointmentEODStatementLines_ds.allowEdit(HMAppointmentEODStatement.Status != HMAppointmentEODStatementStatus::Posted);
    }
    return ret;
}

public int active()
{
    int ret;

    ret = super();
    if(ret)
    {
        HMAppointmentEODStatementLines_ds.object(fieldNum(HMAppointmentEODStatementLines,CustPaymModeTable)).allowEdit(HMAppointmentEODStatementLines.CollectPayment);
        HMAppointmentEODStatementLines_ds.object(fieldNum(HMAppointmentEODStatementLines,PaymReference)).allowEdit(HMAppointmentEODStatementLines.CollectPayment);
    }
    return ret;
}

public void modified()
{
    container   priceCon;
    real        itemListPrice;
    boolean     tradeAgreementExist;
    super();
    if(HMAppointmentEODStatementLines.InventTable_ChargeCode == '')
    {
        HMAppointmentEODStatementLines.ChargeCodeFee = 0.00;
    }
    else
    {
        HMAppointmentEODStatementLines.TaxItemGroup = InventTable::find(HMAppointmentEODStatementLines.InventTable_ChargeCode).inventTableModuleSales().TaxItemGroupId;

        if(HMAppointmentEODStatementClass::chargeCodeExist(HMAppointmentEODStatementLines.InventTable_ChargeCode,HMAppointmentEODStatementLines.NoShowCancellation))
        {
            priceCon = HMSalesOrderInvoiceGenerator::getItemPriceContainerByInventDim(HMAppointmentEODStatementLines.InventTable_ChargeCode, HMAppointmentEODStatementLines.InvoiceAcccount, HMAppointmentEODStatementLines.InventDimId);
            itemListPrice = conPeek(priceCon,1);
            tradeAgreementExist = conPeek(priceCon,6);
            HMAppointmentEODStatementLines.ChargeCodeFee = itemListPrice;
            if(!tradeAgreementExist)
            {
                warning(strFmt("@MAZ11980",HMAppointmentEODStatementLines.InventTable_ChargeCode, InventDim::find(HMAppointmentEODStatementLines.InventDimId).inventLocationName()));
            }
        }
        else
        {
            warning("@MAZ12179");
            HMAppointmentEODStatementLines.InventTable_ChargeCode = '';
            HMAppointmentEODStatementLines.ChargeCodeFee = 0.00;
        }
    }
    element.refreshAmount();
}

[SysClientCacheDataMethodAttribute(true)]
public display str getNoShowCancelReason(HMResourceSchedulingAppointment _HMResourceSchedulingAppointment)
{
    HMAppoinmentLog hmAppointmentLog;
    ReasonTable     reasonTable;
    if(_HMResourceSchedulingAppointment.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Cancelled)
    {
        return enum2str(_HMResourceSchedulingAppointment.HMCancellationStatus);
    }
    else if(_HMResourceSchedulingAppointment.AppointmentStatus == HMResourceSchedulingAppointmentStatus::NoShow)
    {
        select AppointmentReason from hmAppointmentLog
        where hmAppointmentLog.HMResScheduleAppointment == _HMResourceSchedulingAppointment.RecId
        && hmAppointmentLog.HMAppointLogStatus == HMResourceSchedulingAppointmentStatus::NoShow;

        return ReasonTable.findByRecid(hmAppointmentLog.AppointmentReason).Description;
    }
    return '';
}

public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(InventTable), this);

    sysTableLookup.addLookupField(fieldNum(InventTable, ItemId));
    sysTableLookup.addLookupMethod(tableMethodStr(InventTable, itemDescriptionOrName));

    queryBuildDataSource = query.addDataSource(tableNum(InventTable));
    queryBuildDataSource.addSelectionField(fieldNum(InventTable,Product));
    queryBuildDataSource.addDataSource(tableNum(HMInventTable)).relations(true);

    query.dataSourceTable(tableNum(HMInventTable)).addRange(fieldNum(HMInventTable, NoShowCancellation)).value(SysQuery::value(HMAppointmentEODStatementLines.NoShowCancellation));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

