[Form]
public class HMAppointmentNoShowCancelUpdateExamCode extends FormRun
{
    HMResourceSchedulingAppointment HMResourceSchedulingAppointment;
}

public void closeOk()
{
    HMResourceSchedulingHelper HMResourceSchedulingHelper;
    if(ItemId.valueStr() == '')
    {
        warning("@MAZ12418");
        return;
    }

    HMResourceSchedulingHelper = new HMResourceSchedulingHelper();
    HMResourceSchedulingHelper.addNoShowCancelExamCode(HMResourceSchedulingAppointment,ItemId.valueStr());
    super();
    element.close();
}

public void init()
{
    super();
    HMResourceSchedulingAppointment = element.args().record();
    SupplementalInstruction.text(strFmt('%1 - %2',HMResourceSchedulingAppointment.AppointmentNumber,HMResourceSchedulingAppointment.getPatientName()));
}

public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(InventTable), this);

    sysTableLookup.addLookupField(fieldNum(InventTable, ItemId));
    sysTableLookup.addLookupMethod(tableMethodStr(InventTable, itemDescriptionOrName));

    queryBuildDataSource = query.addDataSource(tableNum(InventTable));
    queryBuildDataSource.addSelectionField(fieldNum(InventTable,Product));
    queryBuildDataSource.addDataSource(tableNum(HMInventTable)).relations(true);
    if(HMResourceSchedulingAppointment.AppointmentStatus == HMResourceSchedulingAppointmentStatus::NoShow)
    {
        query.dataSourceTable(tableNum(HMInventTable)).addRange(fieldNum(HMInventTable, NoShowCancellation)).value(SysQuery::value(HMNoShowCancellation::NoShow));
    }
    else if(HMResourceSchedulingAppointment.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Cancelled)
    {
        query.dataSourceTable(tableNum(HMInventTable)).addRange(fieldNum(HMInventTable, NoShowCancellation)).value(SysQuery::value(HMNoShowCancellation::Cancellation));
    }

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}
