public LedgerJournalTable createNewLedgerJournal()
{ 

    //todo: ax 7 remove LedgerJournalTable after uncommnet below code
    LedgerJournalTable ledgerJournalTableLocal;

    //todo: ax7 :AxLedgerJournalTable not exsit

    //AxLedgerJournalTable header = new AxLedgerJournalTable();
    LedgerJournalName ledgerJournalName;
    ledgerJournalName = LedgerJournalName::find(HMBillParameters::getDefaultJournalName());
    if (ledgerJournalName.RecId)
    {
        ttsbegin;
        ledgerJournalTableLocal.initFromLedgerJournalName(ledgerJournalName.JournalName);
        ledgerJournalTableLocal.JournalType = LedgerJournalType::CustPayment;
        ledgerJournalTableLocal.insert();
        ttscommit;
    }
    else
    {
        warning("@MAZ6740");
        return null;
    }


    return LedgerJournalTableLocal;
}

public boolean generateHeaderDateRanges()
{
    str dateFromStr;
    str dateToStr;


    if (FromDate.valueStr() != '' || ToDate.valueStr() != '' )
    {
        if (ToDate.Datevalue() > dateNull() && FromDate.dateValue() > ToDate.Datevalue())
        {
            warning("@MAZ7069");
            FromDate.dateValue(dateNull());
            ToDate.dateValue(dateNull());
            DateSearch.enabled(false);
            return false;
        }

        if (FromDate.dateValue() > dateNull())
            dateFromStr = date2StrXpp(FromDate.dateValue());
        if (ToDate.dateValue() > dateNull())
            dateToStr = date2StrXpp(ToDate.dateValue());

        if (qbrDateHead == null)
        {
            qbrDateHead = element.initHeaderDateRange();
        }
        if (dateFromStr != '' && dateToStr == '')
        {
            dateValStr = strFmt('((AppointmentDate >= %1))', dateFromStr);
        }
        else if (dateToStr != '' && dateFromStr == '')
        {
            dateValStr = strFmt('((AppointmentDate <= %1))', dateToStr);
        }
        else if (dateFromStr != '' && dateToStr != '')
        {
            dateValStr = strFmt('((AppointmentDate >= %1) && (AppointmentDate <= %2))',
            dateFromStr, dateToStr);
        }

        return true;
    }
    return true;
}

