public void init()
{
    Args args;

    super();
    if (this.args())
    {
        args = this.args();
        if (enumNum(HMCaseType) == args.parmEnumType())
        {
            hmCaseType = args.parmEnum();
        }
    }
    element.loadInOutPatientUI();
    qbrDateHead     = element.initHeaderDateRange();
    qbrCaseType     = element.initCaseTypeRange();
    qbrApptStatus   = element.initApptStatusRange();
   // this.setImage();
    ImageWindow.imageList(imageList);

    journalFormTable = LedgerJournalFormTable::construct(element);
    if(element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchOutpatientInvPay))
    {
        this.updateControlsForInvoiceAndPayOrders();
    }
    if (element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchPharmacy))
    {
        this.updateControlsForPharmacy();
    }
}

