public void markDelayed(RecId  _appointmentRecId)
{
    // mark appointment as delayed
    HMResourceSchedulingAppointment     HMResourceSchedulingAppointmentUpdate;

    select forUpdate HMResourceSchedulingAppointmentUpdate
        where HMResourceSchedulingAppointmentUpdate.RecId == _appointmentRecId;

    ttsBegin;
    HMResourceSchedulingAppointmentUpdate.IsDelayed = NoYes::Yes;
    HMResourceSchedulingAppointmentUpdate.update();
    ttsCommit;
}

