public boolean prepareApptReasonsRangeInPatient(Str1260 _strReason)
{
    boolean                             rowsFound = false;
    int                                 x=0,y=0;
    HMResourceSchedulingAppointment     hmResourceSchedulingAppointmentLocal;
    Query                               query;
    QueryBuildDataSource                qbds;
    QueryBuildRange                     queryBuildRange;
    QueryRun                            queryRun;

    query = new Query();
    qbds = query.addDataSource(tableNum(HMResourceSchedulingAppointment));
    queryBuildRange = qbds.addRange(fieldNum(HMResourceSchedulingAppointment, Notes),
    1, QueryRangeType::FullText);
    // The space character is treated as a Boolean OR.
    queryBuildRange.value(_strReason);
    queryRun = new QueryRun(query);

    while (queryRun.next())
    {
        hmResourceSchedulingAppointmentLocal = queryRun.get(
        tableNum(HMResourceSchedulingAppointment));
        //element.removeUnCommonIDs(y);
        reasonIDs += hmResourceSchedulingAppointmentLocal.RecId;
        //reasonIDStr += strFmt(' (RecID = %1) ||', hmResourceSchedulingAppointmentLocal.RecId);
        rowsFound = true;
    }
    element.removeUnCommonIDs();
    if (rowsFound && conLen(reasonIDs) <= 0 )
        rowsFound = false;
    for (y=1; y<= conLen(reasonIDs); y++)
    {

        reasonIDStr += strFmt(' (RecID = %1) ||', conPeek(reasonIDs, y));
    }

    reasonIDStr = subStr(reasonIDStr, 1, strLen(reasonIDStr)-2);
    return rowsFound;
}

public boolean prepareOutPatientPhysicianRanges(DirPartyName _physicianName)
{
    str                                     tmpStr;
    int y;
    HMResourceSchedulingAppointment         hmResourceSchedulingAppointmentLocal;
    HMResourceSchedulingPlanningObject      hmResourceSchedulingPlanningObjectLocal;
    HMClinicStaff                           hmClinicStaffLocal;
    HcmWorker                               hcmWorkerLocal;
    DirPerson                               dirPersonLocal;
    boolean                                 rowsFound = false;

    while select RecId, HMResourceSchedulingPlanningObject from hmResourceSchedulingAppointmentLocal
            join * from hmResourceSchedulingPlanningObjectLocal
            join * from hmClinicStaffLocal
            join * from hcmWorkerLocal
            join * from dirPersonLocal
            where hmResourceSchedulingAppointmentLocal.HMResourceSchedulingPlanningObject == hmResourceSchedulingPlanningObjectLocal.RecId
            && hmResourceSchedulingPlanningObjectLocal.HMClinicStaff == hmClinicStaffLocal.RecId
            && hmResourceSchedulingPlanningObjectLocal.CaseType == hmCaseType
            && hmClinicStaffLocal.HcmWorker == hcmWorkerlocal.RecId
            && hcmWorkerLocal.Person == dirPersonLocal.RecId
            && dirPersonLocal.Name like strFmt('%1*', _physicianName)
        {

            physicianIDs += hmResourceSchedulingAppointmentLocal.RecId;
            rowsFound = true;
        }
    for (y=1; y<= conLen(physicianIDs); y++)
    {
        tmpStr = strFmt(' (RecID = %1) ||', conPeek(physicianIDs, y));
        phyIDStr = phyIDStr + tmpStr;//strFmt(' (RecID = %1) ||', conPeek(physicianIDs, y));
    }
    phyIDStr = subStr(phyIDStr, 1, strLen(phyIDStr)-2);
    return rowsFound;
}

