    }
}

public void refreshGrid(FirstName _firstName,LastName _LastName, FirstName _physicianName,
Container _cSymptoms = conNull(), Str1260 _InPatientReasons = "")
{
    HMMemo                  appIDFilterString;
    boolean                 reasonRows, physicianRows;
    str                     cancelFilter;
    advSearch = true;
    appIDFilterString = '';
    physicianIDs = conNull();
    phyIDStr = '';

    reasonIDs = conNull();
    reasonIDStr = '';

    patientFName = _firstName;
    patientLName = _LastName;
    physicianName = _physicianName;
    strInPReason = _InPatientReasons;
    cSymptoms = _cSymptoms;
    //appointment ranges cleaerd
    HMResourceSchedulingAppointment_1_Ds.queryBuildDataSource().clearRanges();
    //patient ranges cleared
    DirPersonName_1_ds.queryBuildDataSource().clearRanges();
    ///physican range
    PlanningObject_1_ds.query().dataSourceTable(tableNum(DirPerson)).clearRanges();

    reasonRows = false;
    physicianRows = false;

    if (_firstName != '')
    {
        _firstName=strLRTrim(_firstName);
        DirPersonName_1_ds.queryBuildDataSource().
        addRange(fieldNum(DirPersonName,FirstName)).
        value(strFmt('(FirstName LIKE "%1*")', _firstName));
    }
    if (_LastName != '')
    {
        _LastName=strLRTrim(_LastName);
        DirPersonName_1_ds.queryBuildDataSource().
        addRange(fieldNum(DirPersonName,LastName)).
        value(strFmt('(LastName LIKE "%1*")', _lastName));
    }

    if (_physicianName != '')
    {
        physicianRows = element.prepareOutPatientPhysicianRanges(_physicianName);
    }
    else
    {
        physicianRows = true;
    }


    // set true when no criteria set in any Inp or OutP mode
    //symptoms validation removed for medisys
    reasonRows = true;


    element.generateHeaderDateRanges();

    if (strLen(phyIDStr) > 0 && strLen(reasonIDStr) > 0)
        // work for both reason and physicians strings
        appIDFilterString = reasonIDStr;

    else if (strLen(phyIDStr) > 0 && strLen(reasonIDStr) <= 0)
        appIDFilterString = phyIDStr;
        //work for just physicians string

    else if (strLen(phyIDStr) <= 0 && strLen(reasonIDStr) > 0)
        //work for just reasons string
        appIDFilterString = reasonIDStr;

    if (strLen(dateValStr) > 0 && strLen(appIDFilterString))
            appIDFilterString = strFmt('(%2) && (%1)', dateValStr, appIDFilterString);
    else if (strLen(dateValStr) > 0)
        appIDFilterString = strFmt('(%1)', dateValStr);

    if (!reasonRows || !physicianRows)
    {
        if (appIDFilterString == '')
            appIDFilterString = strFmt('(RecId = 0)');
        else
            appIDFilterString = strFmt('(RecId = 0) && (%1)', appIDFilterString);
    }



    if(element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchOutpatientInvPay))
    {
        HMResourceSchedulingAppointment_1_Ds.queryBuildDataSource().
        addRange(fieldNum(HMResourceSchedulingAppointment, AppointmentStatus)).value(SysQuery::value(HMResourceSchedulingAppointmentStatus::InvoiceAndPay));
    }
    else
    {
        //appIDFilterString += strFmt('&& ((%5 == %6) ||  ((%1 != %2) && (%3 != %4)))','HMResourceSchedulingAppointment_1.PrimaryAppointment',enum2int(YesNo::Yes),'HMResourceSchedulingAppointment_1.AppointmentDate','HMPatientEncounter_1.EncounterDate','HMResourceSchedulingAppointment_1.PrimaryAppointment',enum2int(YesNo::Yes));
        appIDFilterString += strFmt('&& (%9 != %11) && ((%9 != %10) || (%9 = %10) && ((%1 = %2) || (%1 = %3) || (%1 = %4) || (%1 = %5) || (%1 = %6) || (%1 = %7) || (%1 = %8)))','HMResourceSchedulingAppointment_1.HMCancellationStatus',
                                                                                                                                                                                            enum2int(HMAppointmentCancellationStatus::WithFeesBusySchedule),
                                                                                                                                                                                            enum2int(HMAppointmentCancellationStatus::WithFeesForceBookConfirmed),
                                                                                                                                                                                            enum2int(HMAppointmentCancellationStatus::WithFeesIllnessWeather),
                                                                                                                                                                                            enum2int(HMAppointmentCancellationStatus::WithFeesNotCoveredPriceTooHigh),
                                                                                                                                                                                            enum2int(HMAppointmentCancellationStatus::WithFeesNotSatisfied),
                                                                                                                                                                                            enum2int(HMAppointmentCancellationStatus::WithFeesOther),
                                                                                                                                                                                            enum2int(HMAppointmentCancellationStatus::WithFeesVacation),
                                                                                                                                                                                            'HMResourceSchedulingAppointment_1.AppointmentStatus',
                                                                                                                                                                                            enum2int(HMResourceSchedulingAppointmentStatus::Cancelled),
                                                                                                                                                                                          enum2int(HMResourceSchedulingAppointmentStatus::ToBeReschedule));

    }
    if (appIDFilterString != '')
            HMResourceSchedulingAppointment_1_Ds.queryBuildDataSource().
            addRange(fieldNum(HMResourceSchedulingAppointment, RecId)).
            value(appIDFilterString);
    HMResourceSchedulingAppointment_1_Ds.executeQuery();
}

