/// <summary>
/// Delete the IDs from resulting container not found int he first container.
/// Do this to ensure the AND operation between the recid by removing un common ids and using only those ids found in both searches
/// </summary>


private void removeUnCommonIDs()
{
    container reasonIDLocal;
    int x=0, y=0;
    RefRecId currId;
    reasonIDLocal = reasonIDs;

    if (strLen(phyIDStr) > 0)
    {
        for (y=1; y<=conLen(reasonIDLocal); y++)
        {
            currId = conPeek(reasonIDLocal,y);
            x = conFind(physicianIDs, conPeek(reasonIDLocal,y));
            if (x <= 0)
            {
                reasonIDs = conDel(reasonIDs,
                conFind(reasonIDs, currId)
                ,1);
            }
        }
    }
}

