public void showTodayAppointments()
{
    str dateFromStr;
    str dateToStr;


    dateFromStr = date2StrXpp(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));
    dateToStr = date2StrXpp(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));

    if (qbrDateHead == null)
    {
        qbrDateHead = element.initHeaderDateRange();
    }
    if (dateFromStr != '' && dateToStr == '')
    {
        dateValStr = strFmt('((AppointmentDate >= %1))', dateFromStr);
    }
    else if (dateToStr != '' && dateFromStr == '')
    {
        dateValStr = strFmt('((AppointmentDate <= %1))', dateToStr);
    }
    else if (dateFromStr != '' && dateToStr != '')
    {
        dateValStr = strFmt('((AppointmentDate >= %1) && (AppointmentDate <= %2))',
        dateFromStr, dateToStr);
    }

    ToDate.dateValue(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));
    FromDate.dateValue(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));
}

