public void init()
{
    QueryBuildDataSource qbdsCaseTrans,qbdsInventTable;
    super();
    if(element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchOutpatientRegular))
    {
        this.query().dataSourceTable(tableNum(HMResourceMaster)).addRange(fieldNum(HMResourceMaster,VendorType)).value(SysQuery::value(HMVendorType::Internal));
    }
    else if(element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchOutpatientExternal))
    {
        this.query().dataSourceTable(tableNum(HMResourceMaster)).addRange(fieldNum(HMResourceMaster,VendorType)).value(SysQuery::value(HMVendorType::External));
    }
    else if(element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchOutpatientDevices))
    {
        this.query().dataSourceTable(tableNum(HMResourceMaster)).addRange(fieldNum(HMResourceMaster,Type)).value(SysQuery::value(HMResourceType::Device));
    }
    else if(element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchOutpatientInvPay))
    {
        this.query().dataSourceTable(tableNum(HMResourceMaster)).enabled(false);
        //this.query().dataSourceTable(tableNum(HMResourceschedulingTypeCode)).enabled(false);
    }
    if (element.args().menuItemName() == menuitemDisplayStr(HMAppointmentSearchPharmacy))
    {
        qbdsCaseTrans = this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointment)).addDataSource(tableNum(HMCaseTrans));
        qbdsCaseTrans.relations(true);
        qbdsCaseTrans.joinMode(JoinMode::ExistsJoin);
        qbdsCaseTrans.addRange(fieldNum(HMCaseTrans,Urgency)).value(SysQuery::value(HMUrgency::Routine));
        qbdsInventTable = qbdsCaseTrans.addDataSource(tableNum(InventTable));
        qbdsInventTable.addLink(fieldNum(InventTable, ItemId), fieldNum(HMCaseTrans, ItemId));
        qbdsInventTable.addRange(fieldnum(InventTable, HMProductType)).value(queryValue(HMInventType::Medication));
        qbdsCaseTrans.addRange(fieldNum(HMCaseTrans,RecId)).value('((HMServiceStatus != 16) && (HMServiceStatus != 19))');
        //qbdsCaseTrans.addRange(fieldNum(HMCaseTrans,HMServiceStatus)).value(SysQuery::valueNot(enum2str(HMServiceStatus::Cancelled)));
    }

}

