public void displayOption(Common _record, FormRowDisplayOption _options)
{
    HMResourceSchedulingAppointment HMResourceSchedulingAppointmentLocal =  HMResourceSchedulingAppointment::find(_record.RecId);

    if (HMResourceSchedulingAppointmentLocal.RecId)
    {
        if (HMPatient::find(HMResourceSchedulingAppointmentLocal.HMPatient).IsVIP)
        {
            _options.backColor((HMColourCodingParameters::findByElementNameAndValue(extendedTypeStr(HMVIP),enum2str(NoYes::Yes)).ColourCode));
        }
        else
        {
            if (HMResourceSchedulingAppointmentLocal.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Open && HMResourceSchedulingAppointmentLocal.IsDelayed == NoYes::Yes)
            {
                _options.backColor((HMColourCodingParameters::findByElementNameAndValue(extendedTypeStr(HMDelayed),enum2str(HMResourceSchedulingAppointmentLocal.IsDelayed)).ColourCode));
                _options.affectedElementsByControl(ImageWindow.id());
            }
            else
            {
                _options.backColor((HMColourCodingParameters::findByElementNameAndValue(enumStr(HMResourceSchedulingAppointmentStatus),enum2str(HMResourceSchedulingAppointmentLocal.AppointmentStatus)).ColourCode));
                _options.affectedElementsByControl(ImageWindow.id());
            }

        }


         super(_record, _options);
    }

}

