public int active()
{
    int ret;

    ret = super();
    if(ret)
    {
        HMAppointmentServiceDialog.enabled(true);
        HMAppointmentNoShowCancelUpdateExamCode.enabled(false);
        HMAppointmentChartPrepare.text(HMResourceSchedulingAppointment_1.ChartPrepared == NoYes::No ? "@MAZ11542" : "@MAZ12295");
        switch(HMResourceSchedulingAppointment_1.AppointmentStatus)
        {
            case HMResourceSchedulingAppointmentStatus::Open:
            case HMResourceSchedulingAppointmentStatus::Confirmed:
                NoShowAppointment.enabled(false);
                DropDialogButton.enabled(false);
                if(HMResourceSchedulingAppointment_1.AppointmentDate < DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone())) //added by faizan
                {
                    CancelAppointment.enabled(false);
                    TransferAppointment.enabled(false);
                    CheckedoutAppointment.enabled(false);
                    CheckedinAppointment.enabled(false);
                    DropDialogButton.enabled(false);
                    NoShowAppointment.enabled(true);
                }
                else if(HMResourceSchedulingAppointment_1.AppointmentDate > DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
                {
                    CheckedoutAppointment.enabled(false);
                    CheckedinAppointment.enabled(false);
                    TransferAppointment.enabled(true);
                    CancelAppointment.enabled(true);
                    if(HMResourceSchedulingAppointment_1.PrimaryAppointment == YesNo::Yes)
                    {
                        DropDialogButton.enabled(true);
                    }
                }
                else if(HMResourceSchedulingAppointment_1.HMConfirmationStatus == HMAppointmentConfirmationStatus::IncompleteInfo)
                {
                    CancelAppointment.enabled(true);
                    TransferAppointment.enabled(true);
                    CheckedoutAppointment.enabled(false);
                    if(HMResourceSchedulingAppointment_1.PrimaryAppointment == YesNo::Yes)
                    {
                        DropDialogButton.enabled(true);
                    }
                    CheckedinAppointment.enabled(false);
                    NoShowAppointment.enabled(true);
                }
                else
                {
                    CancelAppointment.enabled(true);
                    TransferAppointment.enabled(true);
                    CheckedoutAppointment.enabled(false);
                    CheckedinAppointment.enabled(true);
                    if(HMResourceSchedulingAppointment_1.PrimaryAppointment == YesNo::Yes)
                    {
                        DropDialogButton.enabled(true);
                    }
                    NoShowAppointment.enabled(true);
                }

                OpenPatientChart.enabled(true);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                break;

            case HMResourceSchedulingAppointmentStatus::Cancelled:
                CancelAppointment.enabled(false);
                TransferAppointment.enabled(false);
                CheckedoutAppointment.enabled(false);
                CheckedinAppointment.enabled(false);
                DropDialogButton.enabled(false);
                NoShowAppointment.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                HMAppointmentNoShowCancelUpdateExamCode.enabled(true);
                break;

            case HMResourceSchedulingAppointmentStatus::CheckedIn:
                CancelAppointment.enabled(false);
                TransferAppointment.enabled(false);
                CheckedoutAppointment.enabled(true);
                CheckedinAppointment.enabled(false);
                DropDialogButton.enabled(false);
                NoShowAppointment.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(true);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                break;

            case HMResourceSchedulingAppointmentStatus::CheckedOut:
                CancelAppointment.enabled(false);
                TransferAppointment.enabled(false);
                CheckedoutAppointment.enabled(false);
                CheckedinAppointment.enabled(false);
                NoShowAppointment.enabled(false);
                DropDialogButton.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(true);
                break;

            case HMResourceSchedulingAppointmentStatus::Examined:
                CancelAppointment.enabled(false);
                TransferAppointment.enabled(false);
                CheckedoutAppointment.enabled(true);
                CheckedinAppointment.enabled(false);
                DropDialogButton.enabled(false);
                 NoShowAppointment.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                break;

            case HMResourceSchedulingAppointmentStatus::NoShow: //added by faizan
                CancelAppointment.enabled(false);
                TransferAppointment.enabled(false);
                CheckedoutAppointment.enabled(false);
                CheckedinAppointment.enabled(false);
                DropDialogButton.enabled(false);
                NoShowAppointment.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                HMAppointmentNoShowCancelUpdateExamCode.enabled(true);
                break;

            case HMResourceSchedulingAppointmentStatus::Tentative:
                CancelAppointment.enabled(true);
                TransferAppointment.enabled(true);
                CheckedoutAppointment.enabled(false);
                CheckedinAppointment.enabled(false);
                DropDialogButton.enabled(true);
                NoShowAppointment.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                break;
            case HMResourceSchedulingAppointmentStatus::Closed:
                CancelAppointment.enabled(false);
                TransferAppointment.enabled(false);
                CheckedoutAppointment.enabled(false);
                CheckedinAppointment.enabled(false);
                NoShowAppointment.enabled(false);
                DropDialogButton.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                HMAppointmentServiceDialog.enabled(false);
                break;
            case HMResourceSchedulingAppointmentStatus::Reserved:
                CancelAppointment.enabled(true);
                TransferAppointment.enabled(true);
                CheckedoutAppointment.enabled(false);
                CheckedinAppointment.enabled(false);
                NoShowAppointment.enabled(true);
                DropDialogButton.enabled(false);
                HMResourceSchedulingAppointUndoCheckin.enabled(false);
                HMResourceSchedulingAppointUndoCheckout.enabled(false);
                HMAppointmentServiceDialog.enabled(false);
                break;
        }

        if (HMResourceSchedulingAppointment_1.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Open
            || HMResourceSchedulingAppointment_1.AppointmentStatus == HMResourceSchedulingAppointmentStatus::CheckedIn
            || HMResourceSchedulingAppointment_1.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Tentative)
        {
            if (HMResourceSchedulingAppointment_1.IsDelayed)
            {
                 DelayAppointment.enabled(false);
            }
            else
            {
                DelayAppointment.enabled(true);
            }

        }
        else
        {
            DelayAppointment.enabled(false);
        }
    }

    //HMAppointmentLabelReportController
    //switch(HMResourceSchedulingAppointment_1.ComplimentType)
    //{
        //case HMComplementType::ExecutiveHealth:
            //HMAppointmentLabelReportController.enabled(true);
            //break;
        //case HMComplementType::OccupationalHealth:
            //HMAppointmentLabelReportController.enabled(true);
            //break;
        //default:
            //HMAppointmentLabelReportController.enabled(true);
            //break;
    //}

    if(HMResourceSchedulingAppointment_1.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Confirmed
        || HMResourceSchedulingAppointment_1.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Tentative)
    {
        DropDialogButton.enabled(true);
    }
    else
    {
        DropDialogButton.enabled(false);
    }
    return ret;
}

public void executeQuery()
{
    Query q;
    if (hmCaseType == hmCaseType::InPatient || hmCaseType == hmCaseType::OutPatient )
    {
        if (qbrCaseType == null)
            qbrCaseType = element.initCaseTypeRange();
        qbrCaseType.value(SysQuery::value(hmCaseType));
    }

    qbrApptStatus = element.initApptStatusRange();
    //qbrApptStatus.value(SysQuery::valueNot(HMResourceSchedulingAppointmentStatus::Cancelled));

    this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointment)).addRange(fieldNum(HMResourceSchedulingAppointment,HMAppointmentType)).value(SysQuery::valueNot(HMAppointmentType::Admission));
    //this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointment)).addRange(fieldNum(HMResourceSchedulingAppointment,HMConfirmationStatus)).value(SysQuery::valueNot(HMAppointmentConfirmationStatus::IncompleteInfo));
    q = this.query();
    //this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointment)).addRange(fieldNum(HMResourceSchedulingAppointment,PrimaryAppointment)).value(SysQuery::value(YesNo::Yes));
    super();

}

