void clicked()
{
    HMCaseTrans hmcasetrans;
    boolean     shouldNotCheckIn = true;

    if(HMResourceSchedulingAppointment_1.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Confirmed)
    {
        if(HMResourceSchedulingAppointment_1.ActualArrivalTime != -1)
        {
            if(HMResourceSchedulingAppointment_1.PrimaryAppointment == YesNo::Yes)
            {
                select count(RecId) from hmcasetrans
                    where hmcasetrans.HMResourceSchedulingAppointment == HMResourceSchedulingAppointment_1.RecId
                        && hmcasetrans.BillingStatus != HMBillStatus::Paid;
            }
            else
            {
                select count(RecId) from hmcasetrans
                    where hmcasetrans.RelatedAppointmentRecId == HMResourceSchedulingAppointment_1.RecId
                        && hmcasetrans.BillingStatus != HMBillStatus::Paid;
            }

            if(hmcasetrans.RecId > 0)
            {
                shouldNotCheckIn = true;
                throw info ("All of the services are not paid");
            }
            else
            {
                shouldNotCheckIn = false;
            }
        }
        else
        {
            throw info("Arrival time not set ");
        }
    }
    else
    {
        throw info("Appointment status is not conform");
    }

    if (!shouldNotCheckIn)
    {
        super();
    }

}

